import "./promo.css";
import "./promo.js"
 

const Promo = () => {
   rotate.textrotator({
    animation: "flipUp", // You can pick the way it animates when rotating through words. Options are dissolve (default), fade, flip, flipUp, flipCube, flipCubeUp and spin.
    separator: ",", // If you don't want commas to be the separator, you can define a new separator (|, &, * etc.) by yourself using this field.
    speed: 2000 // How many milliseconds until the next word show.
  });

 
  return (
    <section id="home" class="promo-section">

    <div class="container">
        <div class="promo-content">
            <h1>KANIN ROMAN</h1>
            <h2><span class={rotate}>E N G I N E E R, WEB D E V E L O P E R </span></h2 >
            <p>Инженер и WEB разработчик, 
              <br/> делаю вещи и заставляю их работать.
               Воплощая ваши мечты в реальность.</p>
            <ul class="social-link">
                
                
                
            </ul>
        </div>
    </div>
    <div class="down-arrow">
        <a data-scroll href="#about" class="arrow-animated">Scroll Down <i class="ti-arrow-down"></i></a>
    </div>
</section>
  );
};

export default Promo;
