import "./block1.css";



const Block1 = () => {
  const chargeButton = document.getElementById('charge-button');
    const chargeMessage = document.getElementById('charge-message');

    chargeButton.addEventListener('click', () => {
      chargeMessage.classList.remove('hidden');
      chargeButton.disabled = true;
    });
  return (
   


    <section className="block1"> 
        <main class="main">
          { /* Section 1 */}
          

          { /* Section 2 */}
            <div class="section">
              <h2>Энергия везде, где ты</h2>
              <p>Солнечные панели, батареи и свет — всё в твоем распоряжении.</p>
              <button class="button" id="charge-button">Зарядить автодом</button>
              <p id="charge-message" class="hidden">Ты не просто заряжаешь батареи — ты заряжаешься впечатлениями!</p>
            </div>

            { /* Section 3 */}
            <div class="section">
              <h2>Электрические мечты</h2>
              <ul>
                <li>Электродом с умной системой управления.</li>
                <li>Ветряные генераторы на крыше автодома.</li>
                <li>Электровелосипед как часть комплекта.</li>
              </ul>
            </div>

            { /* Section 4 */}
            <div class="section">
              <h2>С Днем Рождения!</h2>
              <p>Ты — источник света и энергии для всех нас. Пусть твои дороги всегда будут гладкими, а запасы энергии неиссякаемыми!</p>
            </div>
      </main>
      
</section>
  );
};

export default Block1;
